void initializeFielObject(fieldObject_t *obj, int objId){
    obj->id = objId;
    obj->facingLeft = 1;
    obj->posX = 0;
    obj->shealdAmmount = 0;
    if(obj->spritesIds != NULL){
        free(obj->spritesIds);
    }

    switch (objId){
    case FIELD_OBJECT_ID_FIGHTER_HERO:
        obj->name = L"fighter";
        obj->maxHealt = 8;
        obj->currHealth = obj->maxHealt;
        obj->teamId = TEAM_ID_ALLAY;
        obj->spritesIds = malloc(sizeof(int)*2);
        (obj->spritesIds)[0] = 1;
        (obj->spritesIds)[1] = 2;
        break;
    case FIELD_OBJECT_ID_ARCHER_HERO:
        obj->name = L"archer";
        obj->maxHealt = 7;
        obj->currHealth = obj->maxHealt;
        obj->teamId = TEAM_ID_ALLAY;
        obj->spritesIds = malloc(sizeof(int)*2);
        (obj->spritesIds)[0] = 3;
        (obj->spritesIds)[1] = 4;
        break;
    case FIELD_OBJECT_ID_THIEF_HERO:
        obj->name = L"thief";
        obj->maxHealt = 7;
        obj->currHealth = obj->maxHealt;
        obj->teamId = TEAM_ID_ALLAY;
        obj->spritesIds = malloc(sizeof(int)*2);
        (obj->spritesIds)[0] = 5;
        (obj->spritesIds)[1] = 6;
        break;
    case FIELD_OBJECT_ID_MAGE_HERO:
        obj->name = L"mage";
        obj->maxHealt = 6;
        obj->currHealth = obj->maxHealt;
        obj->teamId = TEAM_ID_ALLAY;
        obj->spritesIds = malloc(sizeof(int)*2);
        (obj->spritesIds)[0] = 7;
        (obj->spritesIds)[1] = 8;
        break;
    case FIELD_OBJECT_ID_PALADIN_HERO:
        obj->name = L"paladin";
        obj->maxHealt = 10;
        obj->currHealth = obj->maxHealt;
        obj->teamId = TEAM_ID_ALLAY;
        obj->spritesIds = malloc(sizeof(int)*2);
        (obj->spritesIds)[0] = 9;
        (obj->spritesIds)[1] = 10;
        break;
    
    default:
        break;
    }


}
fieldObject_t * createFieldObject(int objId){
    fieldObject_t * obj = malloc(sizeof(fieldObject_t));
    obj->spritesIds = NULL;
    initializeFielObject(obj,objId);
    return obj;
}
void destroyFieldObject(fieldObject_t *obj){
    free(obj->spritesIds);
    free(obj);
}





