void initializeEventsChances(map_events_chances_t *chances, int difficulty){
    int i;
    switch (difficulty)
    {
    case 1:
            chances->values[EVENT_NORMAL_FIGHT] = 65;
            chances->values[EVENT_HARD_FIGHT] = 20;
            chances->values[EVENT_BOSS_FIGHT] = 0;
            chances->values[EVENT_ANVIL] = 15;
            chances->values[EVENT_RANDOM_EVENT] = 15;
            chances->values[EVENT_REST_SITE] = 10;
            chances->values[EVENT_CHEST] = 2;
            chances->values[EVENT_SHOP] = 2;
            chances->values[EVENT_EMPTY] = 0;
        break;
    
    default:
        break;
    }

    chances->curTot = 0;
    for(i = 0; i < TOTAL_MAP_EVENTS; i++){
        chances->curTot += chances->values[i];
    }
}

void initializeSpecificMapEvent(map_event_t *event, int id){
    event->id = id;
    wchar_t *str = event->name;
    switch (event->id)
    {
    case EVENT_NORMAL_FIGHT:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"normal fight");
            event->spriteId = 0;
            event->spriteSelectdId = 1;
        break;
    case EVENT_HARD_FIGHT:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"hard fight");
            event->spriteId = 2;
            event->spriteSelectdId = 3;
        break;
    case EVENT_BOSS_FIGHT:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"boss fight");
            event->spriteId = 4;
            event->spriteSelectdId = 5;
        break;
    case EVENT_ANVIL:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"anvil");
            event->spriteId = 6;
            event->spriteSelectdId = 7;
        break;
    case EVENT_RANDOM_EVENT:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"random event");
            event->spriteId = 8;
            event->spriteSelectdId = 9;
        break;
    case EVENT_REST_SITE:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"rest site");
            event->spriteId = 10;
            event->spriteSelectdId = 11;
        break;
    case EVENT_CHEST:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"chest");
            event->spriteId = 12;
            event->spriteSelectdId = 13;
        break;
    case EVENT_SHOP:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"shop");
            event->spriteId = 14;
            event->spriteSelectdId = 15;
        break;
    case EVENT_EMPTY:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"empty");
            event->spriteId = 16;
            event->spriteSelectdId = 17;
        break;
    default:
            swprintf(str,MAP_EVENT_NAME_MAX_LENGTH,L"error");
            event->spriteId = 16;
            event->spriteSelectdId = 17;
    break;
    }

}
void initializeRandomMapEvent(map_events_chances_t *chances,map_event_t *event, int randId){
    int id = 0;
    randId = randId % chances->curTot;

    while (randId >= chances->values[id]){// "=" needed in ">="
        randId-=chances->values[id];
        id++;
    }

    //id = 0;//debug
    initializeSpecificMapEvent(event, id);
}


